import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';

const User = ({}) => {
    // 로그인 데이터 정의
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const Myfunction = () => {
        // setStatus(true);
        dispatch({
            type: 'SET_TOKEN',
            payload: true,
        }); // console.log('!!!:', status);
    };

    // 로그인처리

    const dispatch = useDispatch();
    const token = useSelector((state) => state.token);

    useEffect(() => {
        console.log('!!!:', token);
    }, [token]);

    return (
        <div className="data">
            <button styles="justify-content: center; align-items: center" onClick={Myfunction}>
                토큰 저장
            </button>

            <button
                styles="justify-content: center; align-items: center"
                onClick={() => {
                    dispatch({
                        type: 'CLEAR_TOKEN',
                    });
                }}
            >
                클리어 토큰
            </button>
        </div>
    );
};

export default User;
